mapper.insert:
		INSERT INTO fabric (
			seq
			, fabric_no
			, fabric_name
			, register_id
			, register_date
		) VALUE (
			 #{seq}
			 , #{fabricNo}
			 , #{fabricName}
			 , #{registerId}
			 , NOW()
		)

mapper.update:
		UPDATE fabric
		SET
			fabric_no = #{fabricNo}
			, fabric_name = #{fabricName}
		WHERE
			seq = #{seq}
