mapper.insert:
		INSERT INTO purchase (
			 purchase_date
			, company_seq
			, total
			, bank
			, cash
			, bill_flag
			, page_flag
			, register_id
			, register_date
		) VALUE (
			 #{purchaseDate}
			, #{companySeq}
			, #{total}
			, #{bank}
			, #{cash}
			, #{billFlag}
			, #{pageFlag}
			, #{registerId}
			, NOW()
		)

mapper.update:
		UPDATE purchase
		SET
			purchase_date = #{purchaseDate}
			, company_seq = #{companySeq}
			, total = #{total}
			, bank = #{bank}
			, cash = #{cash}
			, bil_flag = #{billFlag}
			, page_flag = #{pageFlag}
		WHERE
			seq = #{seq}
