mapper.insert:
		INSERT INTO sales (
			seq
			, sale_date
			, sale_flag
			, fabric_seq
			, section
			, color
			, fabric_count
			, deduction
			, unit
			, price
			, tax
			, delivery
			, receivable
			, etc
			, order_no
			, register_id
			, register_date
		) VALUE (
			 #{seq}
			, #{saleDate}
			, #{saleFlag}
			, #{fabricSeq}
			, #{section}
			, #{color}
			, #{fabricCount}
			, #{deduction}
			, #{unit}
			, #{price}
			, #{tax}
			, #{delivery}
			, #{receivable}
			, #{etc}
			, #{orderNo}
			, #{registerId}
			, #{registerDate}
		)

mapper.update:
		UPDATE sales
		SET
			sale_date = #{saleDate}
			, sale_flag = #{saleFlag}
			, fabric_seq = #{fabricSeq}
			, section = #{section}
			, color = #{color}
			, fabric_count = #{fabricCount}
			, deduction = #{deduction}
			, unit = #{unit}
			, price = #{price}
			, tax = #{tax}
			, delivery = #{delivery}
			, receivable = #{receivable}
			, etc = #{etc}
			, order_no = #{orderNo}
			, register_id = #{registerId}
			, register_date = #{registerDate}
		WHERE
			seq = #{seq}
